queries.query:
xquery version "1.0-ml";
import module namespace sem = "http://marklogic.com/semantics" at "/MarkLogic/semantics.xqy";
let $store := sem:ruleset-store(("rdfs-full.rules", "rdfs.rules", "range.rules", "domain.rules", "subClassOf.rules", "subPropertyOf.rules", "baseball.rules"), (sem:store(), ()))
let $query := ' prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> prefix bb: <http://marklogic.com/baseball/players#> prefix bbr: <http://marklogic.com/baseball/rules#> prefix xs: <http://www.w3.org/2001/XMLSchema#>
construct { ?s bbr:coachType ?long_name . ?s bb:firstname ?firstname . ?s bb:lastname ?lastname . ?s bb:staffPositions ?staff_pos . ?s bb:staffDesc ?staff_desc . } WHERE { ?s bbr:coachingRole bbr:benchCoach . ?s bb:firstname ?firstname . ?s bb:lastname ?lastname . ?s bbr:coachingRole ?role . ?s bb:team ?team . ?role rdfs:label ?staff_desc . ?s ?p ?o . # filter (?team = "Royals") } order by ?team
' let $results := sem:sparql($query, (), (), $store) let $rdf := sem:rdf-serialize($results, "turtle")
return $rdf

