queries.query:
xquery version "1.0-ml";

import module namespace sem = "http://marklogic.com/semantics" at "/MarkLogic/semantics.xqy";

let $store := sem:ruleset-store(("baseball.rules"), sem:store())
let $query := '
prefix  rdf:    <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
prefix  rdfs:   <http://www.w3.org/2000/01/rdf-schema#>
prefix bb:  <http://marklogic.com/baseball/players#>

CONSTRUCT
{
  ?s bb:lastname ?lastname .
  ?s bb:position ?position .
  ?s bb:number ?number .
}
WHERE
{ 
  ?s rdfs:isA "southpaw" .
  ?s bb:lastname ?lastname .
  ?s bb:position ?position .
  ?s bb:number ?number .
}
order by ?lastname
 '
let $results := 
for $result in sem:sparql($query, (), (), $store)
order by sem:triple-subject($result), sem:triple-predicate($result)
return $result

let $rdf := sem:rdf-serialize($results, "turtle")

return $rdf

