xquery version "1.0-ml";
import module namespace sem = "http://marklogic.com/semantics" at "/MarkLogic/semantics.xqy";
let $store := sem:ruleset-store(("baseball.rules", "rdfs-plus-full.rules"), sem:store()) let $query := ' prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> prefix bb: <http://marklogic.com/baseball/players#> prefix bbr: <http://marklogic.com/baseball/rules#> prefix owl: <http://www.w3.org/2002/07/owl#> prefix xs: <http://www.w3.org/2001/XMLSchema#>
construct { ?s ?p ?o . } from <BaseballRules> from <Athletics> WHERE { ?s bb:team ?team . ?team bbr:memberOf bbr:AL . ?s bbr:coachingRole bbr:manager . ?who bbr:coachingRole bbr:manager . ?who bbr:supervises ?coach . ?coach ?p ?o . } order by ?s ?p ' let $triples := sem:sparql($query, (), (), $store) let $rdf := sem:rdf-serialize($triples, "turtle")
return $rdf

