queries.query:
xquery version "1.0-ml";
import module namespace sem = "http://marklogic.com/semantics" at "/MarkLogic/semantics.xqy";
let $store := sem:ruleset-store(("rdfs-full.rules", "rdfs.rules", "range.rules", "domain.rules", "subClassOf.rules", "subPropertyOf.rules", "baseball.rules"), (sem:store(), ())) let $query := ' prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> prefix bb: <http://marklogic.com/baseball/players#> prefix bbr: <http://marklogic.com/baseball/rules#> prefix xs: <http://www.w3.org/2001/XMLSchema#>
CONSTRUCT { ?s bb:lastname ?lastname . ?s bb:position ?position . ?s bb:number ?number . } WHERE { # ?s ?p ?o . ?s rdfs:isA "young" . ?s bb:lastname ?lastname . ?s bb:position ?position . ?s bb:number ?number . ?s bb:team ?team . ?team bbr:memberOf ?league . filter (?league != bbr:FL) } order by ?lastname ' let $results := for $result in sem:sparql($query, (), (), $store) order by sem:triple-subject($result), sem:triple-predicate($result) return $result
let $rdf := sem:rdf-serialize($results, "turtle")
return $rdf

